function sum(a, b) {
  return a + b;
}

def is_palindrome(s):
    t = ''.join(ch.lower() for ch in s if not ch.isspace())
    return t == t[::-1]

const fs = require('fs/promises');

async function readJson(path) {
  const data = await fs.readFile(path, 'utf8');
  return JSON.parse(data);
}

module.exports = { readJson };
